import React from 'react';
import mithishvancouver from '../assets/mithish-vancouver.png';

const About = () => { 
    return (
      <div
        name="about"
        id="about"
        className="h-screen w-full bg-[#000000] flex items-center justify-center"
      >
        <div className="max-w-screen-lg mx-auto flex flex-col md:flex-row-reverse items-center justify-between w-full px-4 py-0">
          {/* Text Section */}
          <div className="flex flex-col justify-center text-center md:text-left md:w-1/2">
            <h2 className="text-4xl sm:text-7xl font-bold text-white">
              About <span className="text-yellow-custom">Me</span>
            </h2>
            <p className="text-white py-4 max-w-md mx-auto md:mx-0">
              Welcome to my little space on the internet! I am a fourth year Computer Science major with a Data Science minor at The University of British Columbia, Okanagan. <br /><br />
              I am passionate about Software Development, Artificial Intelligence, Machine learning, and Aviation! <br /><br />
            </p>
          </div>
          {/* Image Section */}
          <div className="hidden md:flex flex-shrink-0 md:w-1/2 justify-center">
            <img
              src={mithishvancouver}
              alt="my profile"
              className="rounded-2xl w-4/5 md:w-4/5 object-cover"
            />
          </div>
        </div>
      </div>
    );
};

export default About;
